import React, { useState } from 'react';
import axios from 'axios';
import moment from 'moment';
import './WeatherForm.css'; // Import the CSS file

function WeatherForm() {
    const [city, setCity] = useState('');
    const [weather, setWeather] = useState(null);

    const getWeather = async () => {
        try {
            // Get the coordinates for the city
            const coordResp = await axios.get(`http://localhost:3001/getWeatherInfo/city/?city=${city}`);
            const { lat, lon } = coordResp.data[0];

            // Get the weather information using the coordinates
            const weatherResp = await axios.get(`http://localhost:3001/getWeatherInfo/all/?lat=${lat}&lon=${lon}`);
            setWeather(weatherResp.data.current);
        } catch (error) {
            console.error('Error fetching weather data:', error);
        }
    };

    return (
        <div className="weather-form-container">
            <input
                className="weather-form-input"
                type="text"
                value={city}
                onChange={(e) => setCity(e.target.value)}
                placeholder="Enter city name"
            />
            <button className="weather-form-button" onClick={getWeather}>Get Weather</button>
            {weather && (
                <table className="weather-table">
                    <thead>
                        <tr>
                            <th>Field</th>
                            <th>Value</th>
                        </tr>
                    </thead>
                    <tbody>
                        <tr>
                            <td>Temperature</td>
                            <td>{weather.temp} Celcius</td>
                        </tr>
                        <tr>
                            <td>Weather</td>
                            <td>
                                <span className="weather-icon">
                                    <img src={`http://openweathermap.org/img/wn/${weather.weather[0].icon}.png`} alt="Weather Icon" />
                                    <span>{weather.weather[0].main}</span>
                                </span>
                            </td>
                        </tr>
                        <tr>
                            <td>Description</td>
                            <td className="weather-description">{weather.weather[0].description}</td>
                        </tr>
                        <tr>
                            <td>Sunrise</td>
                            <td>{moment.unix(weather.sunrise).format("h:mm a")}</td>
                        </tr>
                        <tr>
                            <td>Sunset</td>
                            <td>{moment.unix(weather.sunset).format("h:mm a")}</td>
                        </tr>
                        <tr>
                            <td>Humidity</td>
                            <td>{weather.humidity}</td>
                        </tr>
                    </tbody>
                </table>
            )}
        </div>
    );
}

export default WeatherForm;
