import React, { useState } from 'react';
import axios from 'axios';
import moment from 'moment';

import Background from '../imag/Weather.jpg';


function WeatherForm() {
    const [city, setCity] = useState('');
    const [weather, setWeather] = useState(null);

    const getWeather = async () => {
        try {
            // Get the coordinates for the city
            const coordResp = await axios.get(`http://localhost:3001/getWeatherInfo/city/?city=${city}`);
            const { lat, lon } = coordResp.data[0];

            // Get the weather information using the coordinates
            const weatherResp = await axios.get(`http://localhost:3001/getWeatherInfo/all/?lat=${lat}&lon=${lon}`);
            setWeather(weatherResp.data.current);
        } catch (error) {
            console.error('Error fetching weather data:', error);
        }
    };

    return (
        <div>
            <div style={{ backgroundImage: `url(require("react-client/src/img/Weather.jpg"))` }}></div>
            <input
                type="text"
                value={city}
                onChange={(e) => setCity(e.target.value)}
                placeholder="Enter city name"
            />
            <button onClick={getWeather}>Get Weather</button>
            {weather && (
                <div>
                    <p>Temperature: {weather.temp} Celcius</p>
                    <p>Weather: {weather.weather[0].main}</p>
                    <p>Description: {weather.weather[0].description}</p>
                    <p>Sunrise: {moment.unix(weather.sunrise).format("h:mm a")}</p>
                    <p>Sunset: {moment.unix(weather.sunset).format("h:mm a")}</p>
                    <p>Humidity: {weather.humidity}</p>
                </div>
            )}
        </div>
    );
}

export default WeatherForm;
